import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { BlogService, Post } from '../../services/blog.service';
import { AuthService } from '../../../../core/services/auth.service';

@Component({
  selector: 'app-blog-list',
  templateUrl: './blog-list.component.html',
  styleUrls: ['./blog-list.component.css'],
})
export class BlogListComponent implements OnInit {
  posts: Post[] = [];
  query = '';

  constructor(
    private blog: BlogService,
    private router: Router,
    public auth: AuthService
  ) {}

  ngOnInit(): void {
    this.refresh();
  }

  refresh() {
    const all = this.blog.list();
    const q = this.query.toLowerCase().trim();
    this.posts = q
      ? all.filter(p =>
          (p.title + ' ' + (p.tags ?? []).join(' ') + ' ' + p.body)
            .toLowerCase()
            .includes(q)
        )
      : all;
  }

  clearSearch() {
    this.query = '';
    this.refresh();
  }

  newPost() {
    // Double-check in TS as well (UI already hides this for non-admins)
    if (!this.auth.isAdmin()) return;
    this.router.navigate(['/blog/new']);
  }

  view(slug: string) {
    this.router.navigate(['/blog', slug]);
  }

  edit(slug: string, e?: MouseEvent) {
    e?.stopPropagation();
    if (!this.auth.isAdmin()) return;
    this.router.navigate(['/blog', slug, 'edit']);
  }

  remove(slug: string, e?: MouseEvent) {
    e?.stopPropagation();
    if (!this.auth.isAdmin()) return;
    if (confirm('Delete this post?')) {
      this.blog.delete(slug);
      this.refresh();
    }
  }

  statusClass(published: boolean): string {
    return published
      ? 'bg-green-100 text-green-800'
      : 'bg-gray-200 text-gray-800';
  }
}
<div class="mx-auto max-w-5xl p-4 sm:p-6">
  <div class="flex flex-col sm:flex-row sm:items-center sm:justify-between gap-3 mb-6">
    <h1 class="text-2xl font-semibold">News & Blog</h1>
    <div class="flex gap-2">
      <input
        type="text"
        [(ngModel)]="query"
        (ngModelChange)="refresh()"
        placeholder="Search posts..."
        class="border rounded-lg px-3 py-2 w-64"
      />
      <button class="px-3 py-2 rounded-lg border" (click)="clearSearch()">Clear</button>

      <!-- Admin-only: New Post -->
      <button
        *ngIf="auth.isAdmin()"
        class="px-3 py-2 rounded-lg bg-black text-white"
        (click)="newPost()"
      >
        New Post
      </button>
    </div>
  </div>

  <div *ngIf="!posts.length" class="text-gray-600">No posts found.</div>

  <div class="grid md:grid-cols-2 gap-4">
    <article
      *ngFor="let p of posts"
      class="rounded-xl border p-4 hover:shadow transition cursor-pointer"
      (click)="view(p.slug)"
    >
      <div class="flex items-center justify-between">
        <h2 class="text-lg font-semibold">{{ p.title }}</h2>
        <span class="text-xs px-2 py-1 rounded-full" [class]="statusClass(p.published)">
          {{ p.published ? 'Published' : 'Draft' }}
        </span>
      </div>
      <p class="text-sm text-gray-500">{{ p.publishedAt | date: 'mediumDate' }}</p>
      <p class="mt-2 line-clamp-3">{{ p.body }}</p>

      <!-- Admin-only actions on each card -->
      <div class="mt-3 flex gap-2" *ngIf="auth.isAdmin()">
        <button class="px-2 py-1 text-sm rounded border" (click)="edit(p.slug, $event)">Edit</button>
        <button class="px-2 py-1 text-sm rounded border" (click)="remove(p.slug, $event)">Delete</button>
      </div>
    </article>
  </div>
</div>
