import { Injectable } from '@angular/core';
import { BehaviorSubject } from 'rxjs';

export interface User {
  id: string;
  name: string;
  email: string;
  roles: string[]; // e.g., ['Admin'] or ['Member']
  token?: string;  // optional mock token
}

const STORAGE_KEY = 'apwu_auth_user_v1';

@Injectable({ providedIn: 'root' })
export class AuthService {
  private _user$ = new BehaviorSubject<User | null>(loadUser());
  /** Observable consumer (header, etc) */
  user$ = this._user$.asObservable();

  /** Snapshot getter for guards/components */
  get user(): User | null {
    return this._user$.getValue();
  }

  /** True if logged in */
  get isLoggedIn(): boolean {
    return !!this.user;
  }

  /** Login with fake checks; add your real API call here later */
  login(email: string, password: string, role: 'Admin' | 'Member' = 'Member'): boolean {
    // Basic validation for demo
    if (!email || !password) return false;

    const mock: User = {
      id: crypto.randomUUID(),
      name: email.split('@')[0],
      email,
      roles: [role],
      token: 'mock-jwt-token.' + btoa(email),
    };

    saveUser(mock);
    this._user$.next(mock);
    return true;
  }

  /** Convenience buttons (can be used from a header, etc.) */
  loginAsAdmin() { return this.login('admin@apwu.local', 'admin', 'Admin'); }
  loginAsMember() { return this.login('member@apwu.local', 'member', 'Member'); }

  logout(): void {
    localStorage.removeItem(STORAGE_KEY);
    this._user$.next(null);
  }

  /** Role helpers */
  hasRole(role: string): boolean {
    return !!this.user?.roles.includes(role);
  }
  hasAnyRole(required: string[]): boolean {
    return required.some(r => this.hasRole(r));
  }
}

function loadUser(): User | null {
  try {
    const raw = localStorage.getItem(STORAGE_KEY);
    if (!raw) return null;
    return JSON.parse(raw) as User;
  } catch {
    return null;
  }
}

function saveUser(u: User) {
  localStorage.setItem(STORAGE_KEY, JSON.stringify(u));
}
