import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { AuthService } from 'src/app/core/services/auth.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html'
})
export class LoginComponent {
  loginForm: FormGroup;
  loading = false;
  error: string | null = null;

  constructor(
    private fb: FormBuilder,
    private authService: AuthService,
    private router: Router
  ) {
    this.loginForm = this.fb.group({
      email: ['', [Validators.required, Validators.email]],
      password: ['', Validators.required]
    });
  }

  onSubmit(): void {
    if (this.loginForm.invalid) return;

    this.loading = true;
    this.error = null;

    this.authService.login(this.loginForm.value).subscribe({
      next: () => {
        this.router.navigate(['/blog']); // Redirect on success
      },
      error: () => {
        this.error = 'Invalid email or password.';
        this.loading = false;
      }
    });
  }
}
<div class="max-w-md mx-auto mt-20 p-6 bg-white rounded-2xl shadow-md space-y-4">
  <h2 class="text-2xl font-bold">Login</h2>

  <form [formGroup]="loginForm" (ngSubmit)="onSubmit()" class="space-y-4">
    <div>
      <label>Email</label>
      <input formControlName="email" type="email" class="w-full p-2 border rounded" />
      <div *ngIf="loginForm.get('email')?.invalid && loginForm.get('email')?.touched" class="text-red-500 text-sm">
        Valid email is required
      </div>
    </div>

    <div>
      <label>Password</label>
      <input formControlName="password" type="password" class="w-full p-2 border rounded" />
      <div *ngIf="loginForm.get('password')?.invalid && loginForm.get('password')?.touched" class="text-red-500 text-sm">
        Password is required
      </div>
    </div>

    <div *ngIf="error" class="text-red-600">{{ error }}</div>

    <button type="submit" [disabled]="loading" class="bg-blue-600 text-white py-2 px-4 rounded hover:bg-blue-700">
      {{ loading ? 'Logging in...' : 'Login' }}
    </button>
  </form>
</div>
