import { Injectable } from '@angular/core';
import { CanActivate, ActivatedRouteSnapshot, Router, UrlTree } from '@angular/router';
import { AuthService } from '../services/auth.service';

@Injectable({ providedIn: 'root' })
export class RoleGuard implements CanActivate {
  constructor(private auth: AuthService, private router: Router) {}

  canActivate(route: ActivatedRouteSnapshot): boolean | UrlTree {
    const allowed: string[] = route.data?.['roles'] ?? [];
    const isLoggedIn = this.auth.isLoggedIn;

    if (!isLoggedIn) {
      // not signed in -> go to login
      return this.router.createUrlTree(['/auth/login'], { queryParams: { redirect: route['_routerState']?.url || '/scheduler' } });
    }

    // if no roles specified, allow any authenticated user
    if (!allowed.length) return true;

    // allow only if user has any of the allowed roles
    if (this.auth.hasAnyRole(allowed)) return true;

    // logged in but not permitted -> send home (or a 403 page if you add one)
    return this.router.createUrlTree(['/']);
  }
}
