import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { BlogService, Post } from '../../services/blog.service';

@Component({
  selector: 'app-blog-detail',
  templateUrl: './blog-detail.component.html',
  styleUrls: ['./blog-detail.component.css'],
})
export class BlogDetailComponent implements OnInit {
  post?: Post;

  constructor(
    private route: ActivatedRoute,
    private router: Router,
    private blog: BlogService
  ) {}

  ngOnInit(): void {
    const slug = this.route.snapshot.paramMap.get('slug')!;
    this.post = this.blog.getBySlug(slug);
    if (!this.post) {
      this.router.navigate(['/blog']);
    }
  }

  edit() {
    if (this.post) this.router.navigate(['/blog', this.post.slug, 'edit']);
  }

  delete() {
    if (!this.post) return;
    if (confirm('Delete this post?')) {
      this.blog.delete(this.post.slug);
      this.router.navigate(['/blog']);
    }
  }
}
<div class="mx-auto max-w-3xl p-4 sm:p-6" *ngIf="post as p">
  <div class="flex items-center justify-between gap-3">
    <h1 class="text-3xl font-bold">{{ p.title }}</h1>
    <div class="flex gap-2">
      <button class="px-3 py-2 rounded-lg border" (click)="edit()">Edit</button>
      <button class="px-3 py-2 rounded-lg border" (click)="delete()">Delete</button>
    </div>
  </div>
  <p class="text-gray-500">{{ p.publishedAt | date:'medium' }}</p>

  <div class="prose prose-neutral max-w-none mt-6 whitespace-pre-wrap">
    {{ p.body }}
  </div>

  <div class="mt-6 text-sm text-gray-600">
    <span *ngIf="p.author">By {{ p.author }}</span>
    <ng-container *ngIf="p.tags && p.tags.length">
      • Tags: {{ (p.tags || []).join(', ') }}
    </ng-container>
    <span *ngIf="p.updatedAt"> • Updated: {{ p.updatedAt | date:'medium' }}</span>
  </div>
</div>
