import { bootstrapApplication } from '@angular/platform-browser';
import { provideHttpClient, withInterceptorsFromDi } from '@angular/common/http';
import {
  provideRouter,
  withEnabledBlockingInitialNavigation,
  withInMemoryScrolling,
  withPreloading,
  PreloadAllModules,
  withRouterConfig,
} from '@angular/router';
import { provideAnimations } from '@angular/platform-browser/animations';

import { AppComponent } from './app/app.component';
import { routes } from './app/app.routes';

bootstrapApplication(AppComponent, {
  providers: [
    provideRouter(
      routes,
      withEnabledBlockingInitialNavigation(),          // initialNavigation: 'enabledBlocking'
      withInMemoryScrolling({
        scrollPositionRestoration: 'enabled',          // restores scroll on back/forward
        anchorScrolling: 'enabled',                    // supports #fragment links
      }),
      withRouterConfig({
        paramsInheritanceStrategy: 'always',           // inherit params to children
        onSameUrlNavigation: 'reload',                 // allow reload on same URL
      }),
      withPreloading(PreloadAllModules),               // preload lazy modules after first load
    ),
    provideHttpClient(withInterceptorsFromDi()),
    provideAnimations(),
  ],
}).catch(err => console.error(err));
